import React from 'react'
import { useState, useEffect } from 'react'


const Fetchrepo = () => {

  const [repo, setRepo] = useState('');
  const [loading, setLoading]= use("Getting data")


  const submit =async (e) => {
    e.preventDefault();
    const repos = await fetch(`https://api.github.com/users/${repo}/repos`)
      .then(res => res.json())
      .then(data => {
        console.log(data.map(repo => repo.git_url));
        console.log(data);
        //Show all repo url in div

         var repo_data=`<>
           <h1>${data.git_url}</h1>
         </>`
      });
  };





  return (
    <div>

      <form action="" onSubmit={submit}>
        <input type="text" name="username"
          value={repo}
          onChange={(e) => setRepo(e.target.value)}
          placeholder="Github username" />
        <button type="submit" >Fetch Repos</button>
      </form>
      <div>
       <h1> {
        loading
        }</h1>
      </div>
    </div>
  )
}

export default Fetchrepo;
