import React from 'react'
import { useState, useEffect } from 'react'


const Fetchrepo = () => {

  const [repo, setRepo] = useState('');
  const [loading, setLoading]= useState(false);


  const submit =async (e) => {
    e.preventDefault();
    const repos = await fetch(`https://api.github.com/users/${repo}/repos`)
      .then(res => res.json())
      .then(data => {
        console.log(data.map(repo => repo.git_url));
        console.log(data);
        //Show all repo url in div

         var repo_data=`<>
           <h1>${data.git_url}</h1>
         </>`
      });
  };


   let render;
  if (loading) {
    render = repo.data.map((repo, index) => {
      return (
        <div className="col-md-4 col-sm-12 col-xs-12" key={index}>
          <div className="card">
            <p>
              <img src={repo.avatar} alt="" />
            </p>
            <div className="card-body">
              <h5 className="card-title">{repo.git_url}</h5>
              <p className="card-text">{repo.html_url}</p>
            </div>
          </div>
        </div>
      );
    });
  } else {
    render = <h3>Loading...</h3>;
  }



  return (
    <div>

      <form action="" onSubmit={submit}>
        <input type="text" name="username"
          value={repo}
          onChange={(e) => setRepo(e.target.value)}
          placeholder="Github username" />
        <button type="submit" >Fetch Repos</button>
      </form>
      <div>
       <h1> {
        loading
        }</h1>
      </div>
    </div>
  )
}

export default Fetchrepo;
