import { useEffect, useState } from "react";
import useToggle from "react";

export default function Test() {
  const [repo, setUser] = useState([]);
  const [loading, setLoading] = useState(false);
  const [btn, setBtn] = useState("Get data");
  const getUser = async () => {
    const response = await fetch(`https://api.github.com/users/${repo}/repos`);
    setUser(await response.json());
  };

  useEffect(() => {
    getUser();
  }, []);

  let render;
  if (loading) {
    render = user.data.map((user, index) => {
      return (
        <div className="col-md-4 col-sm-12 col-xs-12" key={index}>
          <div className="card">
            <p>
              <img src={user.avatar} alt="" />
            </p>
            <div className="card-body">
              <h5 className="card-title">{user.first_name}</h5>
              <p className="card-text">{user.last_name}</p>
            </div>
          </div>
        </div>
      );
    });
  } else {
    render = <h3>Loading...</h3>;
  }
  return (
    <div className="App">
      <div class="text-center">{/* <h1>List of user </h1> */}</div>
      <div className="container-fluid mt5">
        <div className="row text-center mt5">{render}</div>
        <div>
          <div class="text-center">
            <button class="btn btn-primary" onClick={setLoading}>
              {btn}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
