import React from 'react'
import { useState, useEffect } from 'react'


const Fetchrepo = () => {

  const [repo, setRepo] = useState('');





  const submit = (e) => {
    e.preventDefault();
    const repos = fetch(`https://api.github.com/users/${repo}/repos`)
      .then(res => res.json())
      .then(data => {
        console.log(data.map(repo => repo.html_url));
        //Show all repo url in html




        const data_url = <>
          <div>
            <h4>
              {
                data.map(repo => repo.html_url)
              }
            </h4>
          </div>
        </>;



      });



  };





  return (
    <div>

      <form action="" onSubmit={submit}>
        <input type="text" name="username"
          value={repo}
          onChange={(e) => setRepo(e.target.value)}
          placeholder="Github username" />
        <button type="submit" >Fetch Repos</button>
      </form>
      <div>
        {data_url}
      </div>
    </div>
  )
}

export default Fetchrepo;
