import React from 'react'
import { useState, useEffect } from 'react'


const Fetchrepo = () => {

  const [repo, setRepo] = useState('');
  const [loading, setLoading]= useState(false);


  const submit =async (e) => {
    var url= "https://github.com/";
    e.preventDefault();
    const repos = await fetch(`https://api.github.com/users/${repo}/repos`)
      .then(res => res.json())
      .then(data => {
        console.log(data.map(repo => repo.git_url));
        console.log(data);
        //Show all repo url in div
        setLoading(data.map(repo=>
          (
            <div>
              <a href={repo.html_url} target="_blank">{repo.full_name}</a>
            </div>
          )

        ))

  })
  }



  return (
    <div>
     <div className="co">
      <div>
          <form className="d-flex justify-content-center" action="" onSubmit={submit}>
        <input
          type="text"
          name="username"
          value={repo}
          onChange={(e) => setRepo(e.target.value)}
          placeholder="Github username"
        />
        <button className="btn btn-primary" type="submit">Fetch Repos</button>
      </form>
      </div>
      <div>
        <ul class="list-group">
          <li class="list-group-item">{loading}</li>
        </ul>
      </div>
     </div>

    </div>
  );
}

export default Fetchrepo;
